query_chart: {
	handler: function(view, results) {
		console.log("chart handler");
		var me = this,
			esri = me.esri,
					map = me.map_inst;

		var _params = results. _params;

		var queryTask = new esri.QueryTask(
			"http://sampleserver1.arcgisonline.com/ArcGIS/rest/services/Specialty/ESRI_StatesCitiesRivers_USA/MapServer/0"
		);
		var query = new esri.Query();
		query.where = "STATE_NAME='GA'";
		query.returnGeometry = true;
		query.outFields = [ "*" ];

		queryTask.execute(query, function(fset) {
			if (!me._query_layer)
			{
				me._query_layer = new esri.GraphicsLayer();
				map.addLayer(me._query_layer);
				me._glayers.push(me._query_layer);
			}

			if (fset.features.length > 0) {
				me._query_layer.clear();
				var defaultFillSymbol = new esri.SimpleFillSymbol(esri.SimpleFillSymbol.STYLE_SOLID, 
					new esri.SimpleLineSymbol(esri.SimpleLineSymbol.STYLE_SOLID, 
					new esri.Color([ 0, 255, 255, 1]), 5), 
					new esri.Color([ 0, 0, 0, 0 ]));

				 var graphic = new esri.Graphic(fset.features[0].geometry, defaultFillSymbol, fset.features[0].attributes, null);
				me._query_layer.add(graphic);
				map.setExtent(fset.features[0].geometry.getExtent());
			}
		},
		function(error) {
			IG$.ShowError("오류가 발생하였습니다.");
		});
	}
},
